import React from 'react';
import './Content.scss';
import Card from "../Card/Card"

export default class Content extends React.Component {

    constructor(props) {
        super(props);
    }

    componentDidMount() {
        window.onscroll = function (ev) {
            if (!this.state.loadingPokemon && (window.innerHeight + window.scrollY) >= document.body.offsetHeight) {
                this.props.fetchPokemon(this.state.allPokemon.length, 20);
            }
        }.bind(this);
    }

    componentWillMount() {
        debugger
        this.props.fetchPokemon( 0, 20);
    }

    showCards() {
        var items = [];
        for (var i = 0; i < this.state.items; i++) {
          items.push(<Card/>);
        }
        return items;
    }

    render() {
        return (
            <div className="content">
                <div className="cards-table"> 
                    {this.showCards()}
                </div>
                {this.state.loading? 
                <p className="App-intro"> loading ...</p>
                : ""}
            </div>
        );
    }
}
