import React from 'react';
import './Content.scss';
import Card from "../Card/Card"

export default class Content extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: 40,
            loading: false,
        }
    }

    componentDidMount() {
        $(window).scroll(function () {
            if (!this.state.loading && $(window).scrollTop() === $(document).height() - $(window).height()) {
                this.props.fetchPokemon(this.state.allPokemon.length, 20);
            }
        }.bind(this));
    }

    componentWillMount() {
        this.props.actions.fetchPokemon( this.state.allPokemon? this.props.allPokemon.length: 0, 20);
    }

    loadItems() {
        debugger
        this.setState({ 
            loading: true 
        });

        setTimeout(() => {
            this.setState({ 
                 items: this.state.items + 40, 
                 loading: false 
            });
        }, 2000);
    }

    showCards() {
        var items = [];
        for (var i = 0; i < this.state.items; i++) {
          items.push(<Card/>);
        }
        return items;
    }

    render() {
        return (
            <div className="content">
                <div className="cards-table"> 
                    {this.showCards()}
                </div>
                {this.state.loading? 
                <p className="App-intro"> loading ...</p>
                : ""}
            </div>
        );
    }
}
