import React from 'react';
import './Content.scss';
import Card from "../Card/Card"

export default class Content extends React.Component {

    constructor(props) {
        super(props);
    }

    componentWillMount() {
        this.props.fetchPokemon( 0, 20 );
    }

    componentDidMount() {
        this.props.fetchPokemon( 0, 20 );
        window.onscroll = function (ev) {
            if (!this.state.loadingPokemon && (window.innerHeight + window.scrollY) >= document.body.offsetHeight) {
                this.props.fetchPokemon(this.state.allPokemon.length, 20);
            }
        }.bind(this);
    }

    renderCards() {
        {this.state.allPokemon.map(pokemon => {
            return <Card 
                pokemon={ pokemon }
            />;
        })}
    }

    render() {
        return (
            <div className="content">
                <div className="cards-table"> 
                    {this.state.allPokemon !== {}? this.renderCards(): ""}
                </div>
                { this.state.loadingPokemon? 
                    <p className="App-intro"> loading ...</p>
                    : ""
                }
            </div>
        );
    }
}
