import React from 'react';
import './Content.scss';
import Card from "../Card/Card"

export default class Content extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: 20,
            loading: false,
        }
    }

    componentDidMount () {
        this.props.fetchPokemon();
    }

    showCards() {
        var items = [];
        for (var i = 0; i < this.state.items; i++) {
          items.push(<Card/>);
        }
        return items;
    }

    render() {
